const  routes =  [ 
    {  path:  "" ,  component:  "minimal-setup"  } , 
    {  path:  "example" ,  component:  "example-view" ,  name:  "Example"  } 
] ; 

// Navigate to the named route 
changeView ( {  name:  "Example"  } ) 





