            component:  "router-demo" , 
            slots:  [ 
                { 
                    'top-bar' :  'regular-top-bar' , 
                } , 
            ] , 
        } , 
        { 
            path:  'special-home' , 
            component:  'router-demo' , 
            slots:  [ 
                { 
                    'top-bar' :  'special-top-bar' , 
                } , 
            ] , 
        } , 
    ] , 
} 

