nx.body:
Name Type Description   path string     
type SimplrRoute = {
/**
_ The URL path for the route
_/
path: string;
/**
_ The view component name for the route
_/
component?: string;
/**
_ Name of the view route
_/
name?: string;
/**
_ An import function for lazy loading. e.g. () => import("./my-view.js")
_/
import?: Function;
/**
_ A guard function to check view access
_/
guard?: Function;
/**
_ An array of subroutes that inherit paths from the parent route
_/
routes?: SimplrRoute[];
/**
_ An array of slots to append to the view component
_/
slots?: {
[x: string]: string | Function;
}[];
/**
_ RegExp pattern to match dynamic route keys
_/
pattern?: {
[x: string]: string;
};
/**
_ Path to redirect to
_/
redirect?: string | {
name: string;
};
}; 




