        } , 
        { 
            name:  "Example" , 
            path:  "example" , 
            component:  "router-example" , 
        } 
    ] 
    transitionSpeed:  400  // in ms 
} 

const  router =  new  SimplrRouter ( routerOptions) ; 
router. init ( ) ; 

