            component:  "router-example" , 
        } , 
        { 
            name:  "Guarded" , 
            path:  "guarded" , 
            component:  "router-example" , 
            guard :  ( )  =>  { 
                return  Math . random ( )  >=  0.5 ; 
            } 
        } , 
        { 
            path:  "not-found" , 
            component:  "not-found-view" 
        } , 
        { 
            path:  "forbidden" , 
            component:  "forbidden-view" 
        } 
    ] , 
} 

